import { chakra } from "@chakra-ui/react";
import NextImage from "next/image.js";
var Image = chakra(NextImage, {
  shouldForwardProp: (prop) => [
    "src",
    "alt",
    "width",
    "height",
    "fill",
    "loader",
    "quality",
    "priority",
    "loading",
    "placeholder",
    "blurDataURL",
    "unoptimized",
    "onLoadingComplete"
  ].includes(prop)
});

export {
  Image
};
